@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0 0% 100%);
  --foreground: hsl(215 25% 27%);
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(215 25% 27%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(215 25% 27%);
  --primary: linear-gradient(135deg, hsl(217 91% 60%), hsl(240 100% 70%));
  --primary-solid: hsl(217 91% 60%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(215 25% 27%);
  --secondary-foreground: hsl(0 0% 100%);
  --muted: hsl(210 40% 98%);
  --muted-foreground: hsl(215 16% 47%);
  --accent: linear-gradient(135deg, hsl(280 100% 70%), hsl(320 100% 75%));
  --accent-solid: hsl(280 100% 70%);
  --accent-foreground: hsl(215 25% 27%);
  --success: hsl(142 76% 36%);
  --warning: hsl(38 92% 50%);
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(214 32% 91%);
  --input: hsl(214 32% 91%);
  --ring: hsl(217 91% 60%);
  --chart-1: hsl(217 91% 60%);
  --chart-2: hsl(159 100% 36%);
  --chart-3: hsl(43 96% 56%);
  --chart-4: hsl(147 79% 42%);
  --chart-5: hsl(341 75% 51%);
  --sidebar: hsl(0 0% 100%);
  --sidebar-foreground: hsl(215 25% 27%);
  --sidebar-primary: hsl(217 91% 60%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(210 40% 98%);
  --sidebar-accent-foreground: hsl(217 91% 60%);
  --sidebar-border: hsl(214 32% 91%);
  --sidebar-ring: hsl(217 91% 60%);
  --font-sans: 'Inter', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 0.5rem;

  /* Enhanced gradients */
  --gradient-primary: linear-gradient(135deg, hsl(217 91% 60%) 0%, hsl(240 100% 70%) 100%);
  --gradient-accent: linear-gradient(135deg, hsl(280 100% 70%) 0%, hsl(320 100% 75%) 100%);
  --gradient-hero: linear-gradient(135deg, hsl(217 91% 60%) 0%, hsl(280 100% 70%) 50%, hsl(320 100% 75%) 100%);
  --gradient-card: linear-gradient(145deg, hsl(0 0% 100%) 0%, hsl(210 40% 98%) 100%);
  --glass-bg: rgba(255, 255, 255, 0.1);
  --glass-border: rgba(255, 255, 255, 0.2);
}

/* Custom dynamic animations */
@keyframes float {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  25% { transform: translateY(-10px) rotate(1deg); }
  50% { transform: translateY(-5px) rotate(-1deg); }
  75% { transform: translateY(-15px) rotate(0.5deg); }
}

@keyframes pulse-glow {
  0%, 100% { box-shadow: 0 0 5px rgba(147, 51, 234, 0.3); }
  50% { box-shadow: 0 0 20px rgba(147, 51, 234, 0.6), 0 0 30px rgba(147, 51, 234, 0.4); }
}

/* Rimossa - consolidata in gradientShift */

@keyframes shimmer {
  0% { transform: translateX(-100%) rotate(45deg); }
  100% { transform: translateX(100%) rotate(45deg); }
}

.animate-float {
  animation: float 8s ease-in-out infinite;
  will-change: transform;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.animate-pulse-glow {
  animation: pulse-glow 3s ease-in-out infinite;
  will-change: box-shadow;
  backface-visibility: hidden;
}

.animate-gradient-shift {
  background-size: 150% 150%;
  animation: gradientShift 8s ease infinite;
  will-change: background-position;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.animate-shimmer {
  animation: shimmer 3s infinite;
  will-change: transform;
  backface-visibility: hidden;
}

.dark {
  --background: hsl(222 84% 4%);
  --foreground: hsl(210 40% 98%);
  --card: hsl(222 84% 5%);
  --card-foreground: hsl(210 40% 98%);
  --popover: hsl(222 84% 4%);
  --popover-foreground: hsl(210 40% 98%);
  --primary: hsl(217 91% 60%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(217 32% 17%);
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(217 32% 17%);
  --muted-foreground: hsl(215 20% 65%);
  --accent: hsl(43 96% 56%);
  --accent-foreground: hsl(210 40% 98%);
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(217 32% 17%);
  --input: hsl(217 32% 17%);
  --ring: hsl(217 91% 60%);
  --sidebar: hsl(222 84% 4%);
  --sidebar-foreground: hsl(210 40% 98%);
  --sidebar-primary: hsl(217 91% 60%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(217 32% 17%);
  --sidebar-accent-foreground: hsl(210 40% 98%);
  --sidebar-border: hsl(217 32% 17%);
  --sidebar-ring: hsl(217 91% 60%);
}

/* Dark mode specific styles */
.dark .glass-ultra {
  background: rgba(0, 0, 0, 0.2);
  backdrop-filter: blur(15px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.1),
    0 0 60px rgba(59, 130, 246, 0.1);
  will-change: transform;
  backface-visibility: hidden;
}

.dark .aurora-bg {
  background: linear-gradient(45deg, 
    rgba(59, 130, 246, 0.15) 0%,
    rgba(147, 51, 234, 0.15) 25%,
    rgba(6, 182, 212, 0.15) 50%,
    rgba(16, 185, 129, 0.15) 75%,
    rgba(59, 130, 246, 0.15) 100%);
  background-size: 250% 250%;
  animation: gradientShift 15s ease infinite;
  will-change: background-position;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.dark .text-shimmer {
  background: linear-gradient(90deg, 
    #7dd3fc 0%, 
    #a78bfa 25%, 
    #fb7185 50%, 
    #fbbf24 75%, 
    #7dd3fc 100%);
  background-size: 200% 200%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradientShift 5s ease infinite;
  will-change: background-position;
  backface-visibility: hidden;
}

/* Enhanced background patterns */
.geometric-pattern {
  background-image: 
    radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(120, 255, 198, 0.2) 0%, transparent 50%);
}

.floating-elements {
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: 1;
}

.floating-elements::before {
  content: '';
  position: absolute;
  top: 10%;
  left: 10%;
  width: 100px;
  height: 100px;
  background: linear-gradient(45deg, rgba(139, 92, 246, 0.3), rgba(219, 39, 119, 0.3));
  border-radius: 50%;
  animation: float 6s ease-in-out infinite;
  will-change: transform;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.floating-elements::after {
  content: '';
  position: absolute;
  top: 70%;
  right: 10%;
  width: 80px;
  height: 80px;
  background: linear-gradient(45deg, rgba(59, 130, 246, 0.4), rgba(147, 51, 234, 0.3));
  border-radius: 30% 70% 70% 30% / 30% 30% 70% 70%;
  animation: float 8s ease-in-out infinite reverse;
  will-change: transform;
  backface-visibility: hidden;
  transform: translateZ(0);
}

/* Rimossa definizione duplicata - usando versione principale */

/* Custom scrollbar for modal */
.scrollbar-thin {
  scrollbar-width: thin;
}

.scrollbar-track-transparent {
  scrollbar-color: rgba(139, 92, 246, 0.3) transparent;
}

/* WebKit scrollbar for modal */
.scrollbar-thin::-webkit-scrollbar {
  width: 8px;
}

.scrollbar-thin::-webkit-scrollbar-track {
  background: transparent;
  border-radius: 10px;
}

.scrollbar-thin::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, rgba(139, 92, 246, 0.4), rgba(219, 39, 119, 0.4));
  border-radius: 10px;
  border: 2px solid transparent;
  background-clip: content-box;
  transition: all 0.3s ease;
}

.scrollbar-thin::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, rgba(139, 92, 246, 0.7), rgba(219, 39, 119, 0.7));
  background-clip: content-box;
}

/* Enhanced gradients - optimized for performance */
.gradient-vibrant {
  background: linear-gradient(135deg, 
    #667eea 0%, 
    #764ba2 25%, 
    #f093fb 50%, 
    #f5576c 75%, 
    #4facfe 100%);
  background-size: 200% 200%;
  animation: gradientShift 6s ease infinite;
  will-change: background-position;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.gradient-cosmic-enhanced {
  background: linear-gradient(135deg, 
    #ff6b6b 0%, 
    #4ecdc4 25%, 
    #45b7d1 50%, 
    #96ceb4 75%, 
    #feca57 100%);
  background-size: 200% 200%;
  animation: gradientShift 8s ease infinite;
  will-change: background-position;
  backface-visibility: hidden;
  transform: translateZ(0);
}

.card-modern {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(12px);
  border: 1px solid rgba(255, 255, 255, 0.3);
  box-shadow: 
    0 25px 50px rgba(0, 0, 0, 0.1),
    0 8px 32px rgba(0, 0, 0, 0.05),
    inset 0 1px 0 rgba(255, 255, 255, 0.6);
  will-change: transform;
  backface-visibility: hidden;
}

.dark .card-modern {
  background: rgba(30, 41, 59, 0.95);
  border: 1px solid rgba(148, 163, 184, 0.2);
  box-shadow: 
    0 25px 50px rgba(0, 0, 0, 0.3),
    0 8px 32px rgba(0, 0, 0, 0.15),
    inset 0 1px 0 rgba(148, 163, 184, 0.1);
  will-change: transform;
  backface-visibility: hidden;
}

@keyframes gradientShift {
  0%, 100% { 
    background-position: 0% 50%;
    transform: translateZ(0);
  }
  50% { 
    background-position: 100% 50%;
    transform: translateZ(0);
  }
}

/* Performance optimizations - reduce animations on slower devices */
@media (prefers-reduced-motion: reduce) {
  .animate-gradient-shift,
  .gradient-animation,
  .text-gradient-animated,
  .aurora-bg,
  .dark .aurora-bg,
  .text-shimmer {
    animation: none !important;
  }

  .floating-particles,
  .float-beautiful,
  .morphing-card,
  .wave-animation {
    animation: none !important;
  }
}

/* Reduced animations for devices with limited GPU */
@media (max-width: 768px) and (prefers-reduced-motion: no-preference) {
  .gradient-animation,
  .aurora-bg,
  .dark .aurora-bg {
    animation-duration: 20s !important;
  }

  .floating-particles,
  .float-beautiful {
    animation-duration: 15s !important;
  }

  .glass-ultra,
  .glass-card-enhanced,
  .glass-card,
  .glass-effect {
    backdrop-filter: blur(5px) !important;
  }
}

/* Enhanced section backgrounds */
.section-decorative {
  position: relative;
}

.section-decorative::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
